select -- 本月回款 sum(ryn_receivable_collection.amount) amounts, -- 应收未回款(应收余额) ryn_receivable_contract.balance_amount as balance_amounts, ryn_receivable_collection.contract_id from ryn_receivable_collection left join ryn_receivable_contract on ryn_receivable_collection.contract_id=ryn_receivable_contract.contract_id
ryn_receivable_collection.contract_id in
#{contractId}
and ryn_receivable_collection.contract_time >= #{indexDTO.startDate}
and ryn_receivable_collection.contract_time <= #{indexDTO.endDate}
group by contract_id

select sum(balance_amount) as balance_amounts from ryn_receivable_contract
staff_id in ( select employee_id from ryn_employee
department_id in ( select department_id from ryn_employee
employee_id = #{weChatIndexDTO.employeeId}
)
)
and contract_time >= #{weChatIndexDTO.startDate}
and contract_time <= #{weChatIndexDTO.endDate}

select sum(contract_amount) as contract_amounts from ryn_receivable_contract
